      &__quantity {
        font-size: $font-size-medium;
        font-weight: 600;
        margin-right: 0.25rem;
      }

      &__unit {
        font-size: $font-size-medium;
        font-weight: 600;
      }

      &__description {
        font-size: $font-size-medium;
      }

      /* DIRECTIONS */
      &-text {
        padding: 0 1rem;
        font-size: $font-size-medium;
      }

      &__publisher {
        font-weight: 600;
      }

      &__icon-arrow-right {
        width: 2rem;
        height: 2rem;
        fill: $color-white-light-secondary;
        margin-left: 0.5rem;
      }
    }

    &__name {
      max-width: 26rem;
      font-size: $font-size-medium;
      font-weight: 600;

      text-overflow: ellipsis;
      overflow: hidden;
      white-space: nowrap;
    }

    &__fav-button {
      height: 2rem;
      width: 2rem;
      border: none;
      background: none;
      outline: none;
      cursor: pointer;
    }

    &__fav-icon-heart-inactive {
      height: 100%;
      width: 100%;
      fill: $color-grey-light;
      transition: all 0.2s;
    }

    &__fav-icon-heart-active {
      height: 100%;
      width: 100%;
      fill: $color-red;
      transition: all 0.2s;
    }

    &__fav-icon-heart-inactive:active {
      fill: $color-red;
      @include clickAnimation;
    }

    &__fav-icon-heart-active:active {
      fill: $color-red;
      @include clickAnimation;
    }

    &__random-text {
      position: absolute;
      top: 10%;
      left: 0;
      background-color: $color-white;
      padding: 0.5rem 1rem;
      border-top-right-radius: 0.5rem;
      border-bottom-right-radius: 0.5rem;
    }

    &__text {
      font-size: $font-size-small;
      font-weight: 600;
      color: $color-grey;
    }
  }
}

