@include respond(phone) {
  .fav-section {
    width: 100%;
    height: 12rem;
    background-color: $color-white-light;
    border-bottom: 0.3rem solid $color-white-light-secondary;
    border-top: 0.3rem solid $color-white-light-secondary;
    margin-bottom: 2rem;
    margin-top: 7rem;

    display: flex;
    overflow: scroll;

    &::-webkit-scrollbar {
      display: none;
    }

    &__recipe-container {
      width: 8.5rem;
      height: 100%;

      overflow-wrap: break-word;
      text-align: center;
      margin: 0 0.7rem;

      display: flex;
      flex-direction: column;
      justify-content: center;
    }

    &__recipe-image-container {
      position: relative;
    }

    &__recipe-image {
      width: 7rem;
      height: 7rem;
      cursor: pointer;
      transform: translateY(-0.25rem);

      border-radius: 50%;
      border: 0.2rem solid $color-white;
      @include shadow;
      transition: all 0.2s;
    }

    &__recipe-image:active {
      transform: translateY(0rem) scale(0.9);
    }

    &__recipe-name {
      max-height: 2rem;
      font-size: $font-size-tiny;
      font-weight: 700;

      text-overflow: ellipsis;
      overflow: hidden;
      white-space: nowrap;
    }

    /* BUTTON */
    &__remove-fav-button {
      width: 2.5rem;
      height: 2.5rem;
      background-color: $color-white;
      position: absolute;
      top: 0;
      left: 70%;

      @include shadow;
      border-radius: 50%;
      border: 0.2rem solid $color-grey-light;
      outline: none;
      cursor: pointer;
      transition: all 0.2s;
    }

    &__remove-fav-button:active {
      @include clickAnimation;
    }

    &__remove-fav-icon-cross {
      width: 100%;
      height: 100%;
      fill: $color-primary;
    }

    /* MESSAGE */
    &__message-container {
      margin: auto;
    }

    &__message-text {
      font-size: $font-size-medium;
      font-weight: 600;
    }

    &__message-icon-sad {
      height: 1.8rem;
      width: 1.8rem;
      fill: $color-primary;
      margin-bottom: -0.4rem;
      margin-left: 0.3rem;
      opacity: 0;

      @include animation(opacityIn, 5s, infinite);
      animation-delay: 1s;
    }
  }
}

