@include respond(phone) {
  .header {
    width: 100%;
    height: 7rem;
    background-color: $color-white;

    display: flex;
    justify-content: space-evenly;
    align-items: center;

    position: fixed;
    top: 0;
    z-index: 10000;
    opacity: 0.9;

    &__search-button {
      height: 3rem;
      width: 3rem;
      background: none;
      border: none;
      outline: none;
      cursor: pointer;
    }

    &__search-input {
      width: 17rem;
      font-family: 'Poppins', sans-serif;
      font-size: $font-size-medium;
      font-weight: 400;
      color: $color-primary;

      outline: none;
      border: 0.2rem solid $color-grey-light;
      border-radius: 2rem;
      padding: 0.5rem 1rem;
      transition: all 0.2s;
    }

    &__search-input:focus {
      @include animation(borderIn, 2s, forwards);
      @include shadow;
      transform: scale(1.05);
      width: 23rem;
    }

    &__search-input::placeholder {
      font-weight: 300;
      color: $color-grey-light;
    }

    &__icon-magnifying-glass {
      height: 100%;
      width: 100%;
      fill: $color-primary;
      transition: all 0.2s;
    }

    &__icon-magnifying-glass:active {
      @include clickAnimation;
    }
  }
}

